import React, { useState } from "react";
import axios from "axios";
import './App.css';

function App() {
  const [query, setQuery] = useState("");
  const [history, setHistory] = useState([]);

  const handleQuerySubmit = async (e) => {
    e.preventDefault();
    if (query.trim() === "") return;

    try {
      const res = await axios.post("http://localhost:5000/query", { query });
      const chatbotResponse = res.data.response;

      setHistory([...history, { query, response: chatbotResponse }]);
      setQuery("");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h1>Chatbot</h1>
      <div className="chat-history">
        {history.map((entry, index) => (
          <div key={index}>
            <p><strong>You:</strong> {entry.query}</p>
            <p><strong>Bot:</strong> {entry.response}</p>
          </div>
        ))}
      </div>
      <form onSubmit={handleQuerySubmit}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Ask a question"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
