import React, { useState } from "react";
import axios from "axios";
import './App.css';

function App() {
  const [query, setQuery] = useState("");
  const [history, setHistory] = useState([]);

  const handleQuerySubmit = async (e) => {
    e.preventDefault();
    if (query.trim() === "") return;

    try {
      const res = await axios.post("http://localhost:5000/query", { query });
      const chatbotResponse = res.data.response;

      setHistory([...history, { query, response: chatbotResponse }]);
      setQuery("");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="app-container">
      <div className="chatbox">
        <div className="chat-header">Anuj Pokharel ChatBot</div>
        <div className="chat-history">
          {history.map((entry, index) => (
            <div key={index} className="chat-entry">
              <div className="user-message">
                <div className="message-bubble user-bubble">{entry.query}</div>
              </div>
              <div className="bot-message">
                <div className="message-bubble bot-bubble">{entry.response}</div>
              </div>
            </div>
          ))}
        </div>
        <form onSubmit={handleQuerySubmit} className="chat-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Type your message..."
          />
          <button type="submit">Ask Me Anything</button>
        </form>
      </div>
    </div>
  );
}

export default App;
