import React, { useState } from "react";
import axios from "axios";
import './App.css';
import ConversationalForm from './Form'; // Import the form

function App() {
  const [query, setQuery] = useState("");
  const [responseData, setResponseData] = useState("");
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);

  const handleQueryChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/query", { query });
      setResponseData(res.data.response);
      if (res.data.response.includes("call me")) {
        setShowForm(true);
      }
      setError("");
    } catch (err) {
      console.error(err);
      setError("Error querying Gemini LLM");
    }
  };

  return (
    <div className="app-container">
      <h1>Chatbot</h1>
      <div className="chat-box">
        <form onSubmit={handleSubmit} className="chat-form">
          <input
            type="text"
            value={query}
            onChange={handleQueryChange}
            placeholder="Ask a question"
          />
          <button type="submit">Ask</button>
        </form>

        {error && <p className="error-message">{error}</p>}
        {responseData && (
          <div className="chat-response">
            <p><strong>Your Query:</strong> {query}</p>
            <p><strong>Response:</strong> {responseData}</p>
          </div>
        )}
      </div>

      {showForm && <ConversationalForm onSubmit={() => setShowForm(false)} />}
      {showConfirmation && <p>Your appointment has been booked successfully!</p>}
    </div>
  );
}

export default App;
