import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');
  const [formData, setFormData] = useState({ name: '', email: '', phone: '', date: '' });

  const handleQuery = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/query', { query });
      setResponse(res.data.response);
    } catch (err) {
      console.error(err);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/submit-form', formData);
      alert('Form submitted successfully');
    } catch (err) {
      alert('Error submitting form');
    }
  };

  return (
    <div>
      <form onSubmit={handleQuery}>
        <input value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Ask a question" />
        <button type="submit">Submit</button>
      </form>
      <div>
        {response && <p>{response}</p>}
      </div>
      <form onSubmit={handleFormSubmit}>
        <input value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} placeholder="Name" />
        <input value={formData.email} onChange={(e) => setFormData({ ...formData, email: e.target.value })} placeholder="Email" />
        <input value={formData.phone} onChange={(e) => setFormData({ ...formData, phone: e.target.value })} placeholder="Phone" />
        <input value={formData.date} onChange={(e) => setFormData({ ...formData, date: e.target.value })} placeholder="Date (e.g., Next Monday)" />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
