import React, { useState } from "react";
import axios from "axios";
import './App.css';

function ConversationalForm({ onSubmit }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [date, setDate] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/submit-form", { name, email, phone, date });
      alert(res.data.message);
      onSubmit(); // Close the form after submission
    } catch (err) {
      alert(err.response?.data || "An error occurred");
    }
  };

  return (
    <div className="form-container">
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit} className="appointment-form">
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="tel"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          placeholder="Phone Number"
          required
          pattern="\d{10}"
        />
        <input
          type="text"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          placeholder="Appointment Date"
          required
        />
        <button type="submit">Book Appointment</button>
      </form>
    </div>
  );
}

function App() {
  const [query, setQuery] = useState("");
  const [responseData, setResponseData] = useState(null);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);

  const handleQueryChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/query", { query });
      setResponseData(res.data.response);
      if (res.data.response.includes("call me")) {
        setShowForm(true);
      }
      setError("");
    } catch (err) {
      console.error(err);
      setError("Error querying Gemini LLM");
    }
  };

  return (
    <div className="chatbot-container">
      <h1>Chatbot</h1>
      <form onSubmit={handleSubmit} className="chat-form">
        <input
          type="text"
          value={query}
          onChange={handleQueryChange}
          placeholder="Ask a question"
        />
        <button type="submit">Submit</button>
      </form>

      {error && <p className="error-message">{error}</p>}
      {responseData && (
        <div className="chat-response">
          <pre>{JSON.stringify(responseData, null, 2)}</pre>
        </div>
      )}

      {showForm && <ConversationalForm onSubmit={() => setShowForm(false)} />}
    </div>
  );
}

export default App;
