import React, { useState } from "react";
import axios from "axios";

function ConversationalForm({ onSubmit }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [date, setDate] = useState("");
  const [wantAppointment, setWantAppointment] = useState(false);
  const [error, setError] = useState("");
  const [step, setStep] = useState(1); // Track conversation step

  const validateForm = () => {
    if (step === 1 && !name) return "Name is required.";
    if (step === 2 && !email) return "Email is required.";
    if (step === 3 && !phone) return "Phone is required.";
    if (step === 4 && !address) return "Address is required.";
    if (step === 5 && !date) return "Appointment date is required.";
    if (!/\S+@\S+\.\S+/.test(email)) return "Invalid email address.";
    if (!/^\d{10}$/.test(phone)) return "Phone number must be 10 digits.";
    return "";
  };

  const handleNextStep = async (e) => {
    e.preventDefault();
    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    if (step === 4 && wantAppointment) {
      const res = await axios.post("http://localhost:5000/parse-date", { text: date });
      setDate(res.data.date);
    }

    if (step === 5) {
      try {
        await axios.post("http://localhost:5000/submit-form", { name, email, phone, address, date });
        alert("Your appointment has been booked.");
        onSubmit(); // Close the form after submission
      } catch (err) {
        alert(err.response?.data || "An error occurred");
      }
    } else {
      setStep(step + 1);
      setError(""); // Clear error message
    }
  };

  return (
    <div className="form-container">
      <h2>Booking Process</h2>
      <form onSubmit={handleNextStep} className="appointment-form">
        {error && <p className="error-message">{error}</p>}
        
        {step === 1 && (
          <>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Name"
              required
            />
          </>
        )}

        {step === 2 && (
          <>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              required
            />
          </>
        )}

        {step === 3 && (
          <>
            <input
              type="tel"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              placeholder="Phone Number"
              required
              pattern="\d{10}"
            />
          </>
        )}

        {step === 4 && (
          <>
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Address"
              required
            />
            <div>
              <label>
                Do you want to book an appointment?
                <input
                  type="checkbox"
                  checked={wantAppointment}
                  onChange={() => setWantAppointment(!wantAppointment)}
                />
              </label>
            </div>
          </>
        )}

        {step === 5 && wantAppointment && (
          <>
            <input
              type="text"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              placeholder="Appointment Date (e.g. Next Monday)"
              required
            />
          </>
        )}

        <button type="submit">{step === 5 ? "Confirm" : "Next"}</button>
      </form>
    </div>
  );
}

export default ConversationalForm;
