import React, { useState } from "react";
import axios from "axios";
import "./App.css"; // Assuming the CSS is imported for styling

function ConversationalForm({ onSubmit }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [date, setDate] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/submit-form", { name, email, phone, date });
      alert(res.data.message);
      onSubmit();
    } catch (err) {
      alert(err.response.data);
    }
  };

  return (
    <div className="chatbox-form">
      <form onSubmit={handleSubmit}>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
        <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} placeholder="Phone Number" required pattern="\d{10}" />
        <input type="text" value={date} onChange={(e) => setDate(e.target.value)} placeholder="Appointment Date" required />
        <button type="submit">Book Appointment</button>
      </form>
    </div>
  );
}

function App() {
  const [query, setQuery] = useState("");
  const [conversation, setConversation] = useState([]);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);

  const handleQueryChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMessage = { type: "user", text: query };
    setConversation([...conversation, userMessage]);

    try {
      const res = await axios.post("http://localhost:5000/query", { query });
      const responseData = res.data.response.candidates[0]?.text || "No response from the server.";
      const botMessage = { type: "bot", text: responseData };
      setConversation([...conversation, userMessage, botMessage]);

      if (responseData.includes("call me")) {
        setShowForm(true);
      }

      setError("");
    } catch (err) {
      console.error(err);
      setError("Error querying Gemini LLM");
    }

    setQuery("");
  };

  return (
    <div className="chat-container">
      <h1>Chatbot</h1>
      <div className="chat-box">
        {conversation.map((msg, index) => (
          <div key={index} className={`chat-message ${msg.type}`}>
            <p>{msg.text}</p>
          </div>
        ))}
        {showForm && <ConversationalForm onSubmit={() => setShowForm(false)} />}
      </div>
      <form onSubmit={handleSubmit} className="chat-input">
        <input
          type="text"
          value={query}
          onChange={handleQueryChange}
          placeholder="Type a message..."
        />
        <button type="submit">Send</button>
      </form>
      {error && <p className="error">{error}</p>}
    </div>
  );
}

export default App;
