import React, { useState, useEffect } from "react";
import axios from "axios";
import './App.css';

function App() {
  const [query, setQuery] = useState("");
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Adding initial suggestions when the app starts
    setHistory([
      { query: "Do you want to book an appointment?", response: "How can I assist you with booking an appointment?" },
      { query: "Would you want to call them?", response: "I can help you with contacting them." },
    ]);
  }, []);

  const handleQuerySubmit = async (e) => {
    e.preventDefault();
    if (query.trim() === "") return;

    try {
      const res = await axios.post("http://localhost:5000/query", { query });
      const chatbotResponse = res.data.response;
      setHistory([...history, { query, response: chatbotResponse }]);
      setQuery("");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="app-container">
      <div className="chatbox">
        <div className="chat-header">Chatbot</div>
        <div className="chat-history">
          {history.map((entry, index) => (
            <div className="chat-entry" key={index}>
              <div className="message-bubble user-message">
                <span className="user-bubble">{entry.query}</span>
              </div>
              <div className="message-bubble bot-message">
                <span className="bot-bubble">{entry.response}</span>
              </div>
            </div>
          ))}
        </div>
        <form className="chat-form" onSubmit={handleQuerySubmit}>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Ask a question"
          />
          <button type="submit">Ask</button>
        </form>
      </div>
    </div>
  );
}

export default App;
