import React, { useState } from "react";
import axios from "axios";
import './Form'
import './App.css';
//import chrono from 'chrono-node'; // For date parsing

function ConversationalForm({ onSubmit }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState(""); // Add address
  const [date, setDate] = useState("");
  const [step, setStep] = useState(1); // Track the current step in the form

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Parse the date provided by the user
      const parsedDate = chrono.parseDate(date);
      if (!parsedDate) {
        alert("Invalid date format");
        return;
      }

      const res = await axios.post("http://localhost:5000/submit-form", { 
        name, email, phone, address, date: parsedDate 
      });
      alert(res.data.message);
      onSubmit(); // Close the form after submission
    } catch (err) {
      alert(err.response.data);
    }
  };

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const renderForm = () => {
    if (step === 1) {
      return (
        <div>
          <label>What is your name?</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
          <button onClick={handleNextStep}>Next</button>
        </div>
      );
    } else if (step === 2) {
      return (
        <div>
          <label>What is your email address?</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
          <button onClick={handleNextStep}>Next</button>
        </div>
      );
    } else if (step === 3) {
      return (
        <div>
          <label>Please provide your phone number</label>
          <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} placeholder="Phone" required />
          <button onClick={handleNextStep}>Next</button>
        </div>
      );
    } else if (step === 4) {
      return (
        <div>
          <label>When do you want to book?</label>
          <input type="text" value={date} onChange={(e) => setDate(e.target.value)} placeholder="Appointment Date (e.g., Next Monday)" required />
          <button onClick={handleNextStep}>Next</button>
        </div>
      );
    } else if (step === 5) {
      return (
        <div>
          <label>What is your address?</label>
          <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Address" required />
          <button type="submit">Submit</button>
        </div>
      );
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {renderForm()}
    </form>
  );
}

function App() {
  const [query, setQuery] = useState("");
  const [responseData, setResponseData] = useState(null);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [history, setHistory] = useState([]); // Store the conversation history

  const handleQueryChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (query.trim() === "") return; // Ignore empty queries

    try {
      const res = await axios.post("http://localhost:5000/query", { query });
      const chatbotResponse = res.data.response;

      // Add the query and response to history
      setHistory((prevHistory) => [
        ...prevHistory,
        { query, response: chatbotResponse }, // Add query and response to history
      ]);

      // Check if response contains intent to "call me"
      if (chatbotResponse.toLowerCase().includes("call me")) {
        setShowForm(true); // Show the form if the response contains "call me"
      }

      setQuery(""); // Clear the input field
      setError(""); // Reset the error state
    } catch (err) {
      console.error(err);
      setError("Error querying Gemini LLM");
    }
  };

  return (
    <div>
      <h1>Chatbot</h1>

      {/* Chat History */}
      <div className="chat-history">
        {history.map((entry, index) => (
          <div key={index} className="chat-entry">
            <div className="query">{entry.query}</div>
            <div className="response">{entry.response}</div>
          </div>
        ))}
      </div>

      {/* Form to handle the user query */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={query}
          onChange={handleQueryChange}
          placeholder="Ask a question"
        />
        <button type="submit">Submit</button>
      </form>

      {/* Error Message */}
      {error && <p>{error}</p>}

      {/* Display the Conversational Form if necessary */}
      {showForm && <ConversationalForm onSubmit={() => setShowForm(false)} />}
    </div>
  );
}

export default App;
